mapper.select:
SELECT y.id as devid , y.devnum as devnum , y.mac as mac , dg.groupname as groupname , y.code as code , dg.managerid as managerid ,t7.`site`,t7.picpath, (SELECT contacts FROM t_user WHERE id=y.`own_id`) AS contacts, y.addtime as addtime , y.lat as lat , y.lng as lng , y.state as state , y.battery as battery ,y.lastValue as lastValue,y.addrcode as addrcode,y.lessalarmvalue as lessalarmvalue, y.topalarmvalue as topalarmvalue,y.alarmset as alarmset, y.x as x,y.y as y, count(v.videoid) as count , y.adduserid as adduserid, y.lastalarmtime as lastalarmtime , y.own_id as own_id FROM t_device_sydl y INNER JOIN t_device_group_relation tg on tg.moduleid = #{moduleid} and tg.deviceid = y.id
and tg.groupid=#{groupid}
INNER JOIN t_device_group dg on dg.id = tg.groupid INNER JOIN t_user_dgroup_relation tt ON tt.devicegroupid=dg.id INNER JOIN t_user u on u.id=tt.userid LEFT JOIN t_dev_video_relation v on v.moduleid= #{moduleid} AND y.id=v.deviceid LEFT JOIN t_dev_item_pic t6 ON t6.`dev_id`=y.`id` AND t6.`moduleid`=#{moduleid} LEFT JOIN t_item_pic t7 ON t6.`item_pic_id`=t7.`id` WHERE y.isenable = 1
and y.addtime >= #{Starttime}
and y.addtime <= #{Endtime}
and (y.devnum LIKE CONCAT('%',#{context},'%') or y.code LIKE CONCAT('%',#{context},'%') or y.mac LIKE CONCAT('%',#{context},'%'))
and y.battery < 20
and y.battery >= 20
group by y.id

SELECT jg.id as id , jg.devnum as devnum , jg.lat as lat , jg.lng as lng , jg.mac as mac,jg.code as code,jg.own_id as own_id, jg.x as x ,jg.y as y ,g.id as groupid,g.groupname as groupname ,p.id as itempicid, p.picpath as picpath , p.site as site,jg.lastalarmtime as lastalarmtime, jg.lastValue as lastValue,jg.addrcode as addrcode,jg.lessalarmvalue as lessalarmvalue,jg.topalarmvalue as topalarmvalue, jg.alarmset as alarmset,GROUP_CONCAT(t666.`videoid`) AS videos FROM t_device_sydl jg LEFT JOIN t_device_group_relation dg on dg.moduleid = #{moduleid} and dg.deviceid = jg.id and dg.isenable = 1 LEFT JOIN t_user_dgroup_relation ug on ug.userid = #{userid} and ug.devicegroupid = dg.groupid LEFT JOIN t_dev_item_pic dp on dp.dev_id = dg.deviceid and dp.moduleid = dg.moduleid LEFT JOIN t_item_pic p on p.id = dp.item_pic_id and p.isenable = 1 LEFT JOIN t_device_group g on g.id = dg.groupid and g.isenable = 1 LEFT JOIN t_dev_video_relation t666 ON jg.`id` = t666.`deviceid` AND t666.`moduleid` = 17 LEFT JOIN t_device_video t888 ON t666.`videoid` = t888.`id` AND t888.`isdelete` = 0 WHERE jg.isenable = 1
and jg.id = #{devid}
and jg.devnum = #{devnum}
and jg.mac = #{mac}
GROUP BY jg.`id`

mapper.update:
UPDATE t_device_sydl jg , t_device_group_relation dg
SET jg.id = jg.id

	, jg.devnum = #{devnum}


	, jg.lat = #{lat}


	, jg.lng = #{lng}


	, jg.x = #{x}


	, jg.y = #{y}


	, jg.code = #{code}


	, jg.own_id = #{ownId}


	, jg.addrcode = #{addrcode}


	, jg.lessalarmvalue = #{lessalarmvalue}


	, jg.topalarmvalue = #{topalarmvalue}


	, dg.groupid = #{groupid}

WHERE jg.id = #{devid} and dg.moduleid = #{moduleid} and dg.deviceid =
#{devid}

mapper.select:
SELECT g.id as id , g.groupname as groupname, g.groupcode as groupcode ,GROUP_CONCAT(p.id) as itempicid , GROUP_CONCAT(p.picpath) as picpath , GROUP_CONCAT(p.site) as site , g.x as lng , g.y as lat , g.itemnum as itemnum , u.contacts as contacts, g.addtime as addtime , g.imgpath as imgpath FROM t_device_group g RIGHT JOIN t_user_dgroup_relation ug on ug.userid = #{userid} and ug.devicegroupid = g.id and ug.isdelete = 0 and ug.isenable = 1 LEFT JOIN t_item_pic p on p.itemid = g.id LEFT JOIN t_user u on u.id = g.managerid WHERE g.isdelete = 0 and g.moduleid = #{moduleid}
and g.id = #{id}
and g.groupname = #{groupname}
and g.itemnum = #{itemnum}
GROUP BY g.id

mapper.update:
UPDATE t_device_sydl t
SET t.mac=#{mac}
WHERE t.id =
#{devid}

